window.addEventListener("DOMContentLoaded",(()=>{const e=document.getElementById("to-waiting"),t=document.querySelector(".header__action-link");t.addEventListener("click",(t=>{t.preventDefault(),e.scrollIntoView({behavior:"smooth"})}));const o=document.querySelectorAll(".button"),n=document.querySelector(".overlay");o.forEach((e=>{e.addEventListener("click",(()=>{n.classList.add("overlay_active"),document.body.style.overflow="hidden"}))}));document.querySelector(".modal__close").addEventListener("click",(()=>{n.classList.remove("overlay_active"),document.body.style.overflow="auto"}))}));